import React from 'react';
import "./Home.css";
function Home(){
    const bgcolor = 'black';
    const handletoggle = ()=>{
        console.log("The button moved!");
        bgcolor:'black';
    }
    
    return(
        <div style = {{backgroundColor:`${bgcolor}`}} className="pt-4">
            <nav className="navbar navbar-expand-lg navbar-light ">
                <div className="container-fluid">
                    <img src="/favicon.ico" alt="" className="logo"/>
                    <a className="navbar-brand">Weather Forecast</a>
                </div>
            </nav>
            {/* Rounded switch  */}
            <label  className="switch" >
                <input type="checkbox" />
                <span className="slider round"id="toggle" onClick={handletoggle}></span>
            </label>
            {/* Search Tab */}
            <div className="container" id = "searchtab">
                    <div className ="col box" id = "boxes">
                        <div className = 'row main'>
                            <h2 id = 'hd'>Hello and welcome to Weather Forecast!</h2> 
                
                        </div>
                        <div className ="row search" >
                            <form className = "check">
                                <div className="mb-3">
                                <label htmlFor="exampleInputEmail1" className="form-label">Enter the city name</label>
                                <input type="text" className="form-control inp" id="exampleInputEmail1" aria-describedby="emailHelp"/>
                                <div id="emailHelp" className="form-text">We'll never share your information with anyone else.</div>
                                </div>
                                <button type="submit" className="btn btn-primary">Check Weather</button>
                            </form>
                    </div> 
                    
                    </div>
            </div>
        </div>
    )
}
export default Home;
