import React,{useState} from 'react'
import "./Togglebutton.scss"
const Togglebutton = () => {
  const [bgcolor, setBgcolor] = useState('white');
  const [textcol, settextcol] = useState('black');
  const [shadowcolor, setshadowcolor] = useState('2px 2px 5px black');

    const handletoggle = ()=>{
        if(bgcolor === 'white'){
            setBgcolor('black');
            settextcol('white');
            setshadowcolor('4px 4px 4px 4px white');
        }
        else{
            setBgcolor('white');
            settextcol('black');
            setshadowcolor('4px 0 4px 4px black');
        }
        
    }
  return (
    <div>
        {/* Rounded switch  */}
        <label  className="switch" >
            <input type="checkbox" />
            <span className="slider round"id="toggle" onClick={handletoggle}></span>
        </label>
    </div>
  )
}

export default Togglebutton
